class Solution:
    def minimumCost(self, m: int, n: int, hor: List[int], ver: List[int]) -> int:
        hor.sort(reverse=True)
        ver.sort(reverse=True)

        h, v = 0, 0
        hcnt, vcnt = 1, 1
        ans = 0

        while h < len(hor) and v < len(ver):
            if hor[h] > ver[v]:
                ans += hor[h] * vcnt
                hcnt += 1
                h += 1
            else:
                ans += ver[v] * hcnt
                vcnt += 1
                v += 1

        while h < len(hor):
            ans += hor[h] * vcnt
            h += 1

        while v < len(ver):
            ans += ver[v] * hcnt
            v += 1

        return ans
