class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int hor = 1, ver = 1;
        vector<pair<int, int>> vp;
        for (auto &x: horizontalCut){
            vp.push_back({x, 1});
        }
        for (auto &x: verticalCut){
            vp.push_back({x, 2});
        }
        sort(vp.rbegin(), vp.rend());
        long long ans = 0;
        for (auto &x: vp){
            int val = x.first;
            int dim = x.second;
            if (dim == 1){
                ans += (val*1LL*hor);
                ver++;
            }
            else {
                ans += (val*1LL*ver);
                hor++;
            }
        }
        return ans;
    }
};
