class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hori, vector<int>& vert) {
        long long cost = 0;

        vector<pair<int, int>> v;
        for(auto i:vert){
            v.push_back({i, 1});
        }
        for(auto i:hori){
            v.push_back({i, 0});
        }
        sort(all(v));
        reverse(all(v));

        long long h=1, ver=1;

        for(auto i:v){
            long long val = i.ff;

            int dir = i.ss;

            if(dir==1){
                // vertical cut
                // depend on hori pieces 
                cost += (val*(h));
                ver++;
            }
            else{
                // hori cut
                // vert pic
                cost += (val*(ver));
                h++;
            }
        }
        return cost;
    }
};
