class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
           vector<pair<long long, char>> cuts;
    for (int cost : horizontalCut) {
        cuts.push_back({cost,'H'});
    }
    for (int cost : verticalCut) {
          cuts.push_back({cost,'V'});
    }
    sort(cuts.begin(), cuts.end(),greater<pair<long long,char>>());


    long long tp = 0;
    long long h = 1;
    long long v = 1;


    for (auto cut : cuts) {
        long long c = cut.first;
        long long t = cut.second;
        if (t == 'H') {
            tp += c * v;
            h++;
        } else { 
            tp += c * h;
            v++;
        }
    }

    for(int i=0;i<100;i++)
    {
        cuts.push_back({i,'H'});
    }

      for(int i=0;i<100;i++)
    {
        cuts.push_back({i,'V'});
    }

    return tp;
    }
};
