class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        int hind=horizontalCut.length-1;
        int vind=verticalCut.length-1;
        int hseg=1;
        int vseg=1;
        long ans=0;
        while(hind>=0 && vind>=0){
            if(horizontalCut[hind]>verticalCut[vind]){
                ans+=horizontalCut[hind]*vseg;
                hind--;
                hseg++;
            }
            else{
                ans+=verticalCut[vind]*hseg;
                vind--;
                vseg++;
            }
        }
        while (hind >= 0) {
            ans += horizontalCut[hind] * vseg;
            hind--;
        }
        while (vind >= 0) {
            ans += verticalCut[vind] * hseg;
            vind--;
        }

        return ans;
    }
}
