class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        h.sort(reverse = True)
        v.sort(reverse=True)
        hp = 1
        vp = 1
        ans = 0
        i,j = 0 ,0
        while i<m-1 and j<n-1:
            if h[i] > v[j]:
                ans+= h[i]*vp
                hp+=1
                i+=1
            else:
                ans+=v[j]*hp
                vp+=1
                j+=1
        if i<m-1:
            ans+= (sum(h[i:]))*vp
        if j<n-1:
            ans+=(sum(v[j:]))*hp
        return ans
