class Solution {
public:
    long long minimumCost(int m, int n, std::vector<int>& H, std::vector<int>& V) {
        sort(H.rbegin(), H.rend());
        sort(V.rbegin(), V.rend());

        int hParts = 1, vParts = 1;
        long long ans = 0;

        int hIndex = 0, vIndex = 0;
        while (hIndex < m - 1 && vIndex < n - 1) {
            if (H[hIndex] > V[vIndex]) {
                ans += (long long)H[hIndex] * vParts;
                hParts++;
                hIndex++;
            } else {
                ans += (long long)V[vIndex] * hParts;
                vParts++;
                vIndex++;
            }
        }

        while (hIndex < m - 1) {
            ans += (long long)H[hIndex] * vParts;
            hIndex++;
        }

        while (vIndex < n - 1) {
            ans += (long long)V[vIndex] * hParts;
            vIndex++;
        }

        return ans;
    }
};
