class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        # 贪心  优先切开销大的

        cuts = [(cost, 'h') for cost in h] + [(cost, 'v') for cost in v]
        cuts.sort(reverse=True)

        h_cnt = v_cnt = 1
        ans = 0

        for c, t in cuts:
            if t == 'h':
                ans += c * v_cnt
                h_cnt += 1
            else:
                ans += c * h_cnt
                v_cnt += 1

        return ans
