class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        rc = [1, 1]
        res = 0
        pq = sorted([(-h, 0) for h in horizontalCut] + [(-v, 1) for v in verticalCut])
        while pq:
            cost, idx = heapq.heappop(pq)
            res += -cost * rc[idx ^ 1]
            rc[idx] += 1
        return res
