class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        long long ans = 0;

        long long sz1 = horizontalCut.size(), sz2 = verticalCut.size();
        long long i = 0, j = 0;
        long long c1 = 1, r1 = 1;

        while(i<sz1 && j<sz2){
            if(horizontalCut[i] > verticalCut[j]){
                ans += (r1*horizontalCut[i]);
                c1++;
                i++;

            }
            else{
                ans += (c1*verticalCut[j]);
                r1++;
                j++;

            }
        }

        while(i<sz1){
            ans += (r1*horizontalCut[i]);
            c1++;
            i++;      
        }

        while(j<sz2){
            ans += (c1*verticalCut[j]);
            r1++;
            j++;
        }

        return ans;
    }
};
