class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());
        long long hc=1,vc=1;
        int i=0,j=0;
        long long ans=0;
        while((i<h.size())&&(j<v.size())){
            if(h[i]>v[j]){
                ans += h[i]*vc;
                hc++;
                i++;
            }
            else{
                ans+=v[j]*hc;
                vc++;
                j++;
            }
        }
        //cout<<i<<" "<<j<<endl;
        while(i<h.size()){
            ans += h[i]*vc;
            hc++;
            i++;
        }
        while(j<v.size()){
           ans+=v[j]*hc;
            vc++;
            j++; 
        }

        return ans;
    }
};
