#define ll long long
class Solution {
public:
   long  long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {

    ll tc = 0;


    vector<pair<int, char>> cuts;
    for (int i = 0; i < m - 1; i++) {
        cuts.push_back({hc[i], 'H'});
    }
    for (int i = 0; i < n - 1; i++) {
        cuts.push_back({vc[i], 'V'});
    }


    sort(cuts.begin(), cuts.end());
       reverse(cuts.begin(),cuts.end());


    int hs = 1;
    int vs = 1;


    for (auto cut : cuts) {
        int cost = cut.first;
        char t = cut.second;
        if (t == 'H') {

            tc += 1ll*cost * vs;
            hs += 1ll;
        } else {

            tc += 1ll*cost * hs;
            vs += 1ll;
        }
    }

    return tc;
}
};
