class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long ans = 0;
        vector<pair<int, int>> cost;
        for (auto i: horizontalCut) {
            cost.push_back({i, 0});
        }
        for (auto i: verticalCut) {
            cost.push_back({i, 1});
        }
        sort(cost.begin(), cost.end(), greater<pair<int, int>>());
        long long r = 1, c = 1;
        for (auto [f, s]: cost) {
            if (s == 1) ans += r * f, ++c;
            else ans += c * f, ++r;
        }
        return ans;
    }
};
