class Solution:
    def minimumCost(self, m: int, n: int, hcuts: List[int], vcuts: List[int]) -> int:
        cuts = []
        for hcut in hcuts:
            cuts.append(hcut)

        for vcut in vcuts:
            cuts.append(-vcut)

        cuts.sort(key=lambda ele: abs(ele))
        nrows = 1
        ncols = 1
        result = 0
        while cuts:
            cut = cuts.pop()
            if cut > 0:
                result += cut * ncols
                nrows += 1
            else:
                result += (-cut) * nrows
                ncols += 1

        return result
