class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<long long, char>> vec;
        for (long long cost : h) vec.emplace_back(cost, 'H');
        for (long long cost : v) vec.emplace_back(cost, 'V');


        sort(vec.rbegin(), vec.rend());


        long long hp = 1;
        long long vp = 1;


        long long mini = 0;
        for (auto& cut : vec) {
            int cost = cut.first;
            char type = cut.second;

            if (type == 'H') {

                mini += 1LL*cost * vp;
                hp++;
            } else {

                mini += 1LL*cost * hp;
                vp++;
            }
        }

        return mini;

    }
};
