class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
    sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
    sort(verticalCut.begin(), verticalCut.end(), greater<int>());

    int h = 0, v = 0; 
    long long horizontalPieces = 1, verticalPieces = 1; 
        long long totalCost = 0;
    while (h < horizontalCut.size() && v < verticalCut.size()) {
        if (horizontalCut[h] >= verticalCut[v]) {
            totalCost += static_cast<long long>(horizontalCut[h]) * verticalPieces;
            horizontalPieces++;
            h++;
        } else {
            totalCost += static_cast<long long>(verticalCut[v]) * horizontalPieces;
            verticalPieces++;
            v++;
        }
    }


    while (h < horizontalCut.size()) {
        totalCost += static_cast<long long>(horizontalCut[h]) * verticalPieces;
        h++;
    }

    while (v < verticalCut.size()) {
        totalCost += static_cast<long long>(verticalCut[v]) * horizontalPieces;
        v++;
    }

    return totalCost;
    }
};
