class Solution {
public:
    #define ll long long
    ll solve(int h, int v, int &m, int &n, vector<int>& hr, vector<int>& vr){
        if(h==m && v==n)
            return 0;
        if(h>=m || v>=n){
            ll ans = 0;
            if(h>=m){
                for(int i=v;i<n;i++){
                    ans += (vr[i-1]*1LL*(h));
                }
            }
            else{
                for(int i=h;i<m;i++){
                    ans += (hr[i-1]*1LL*(v));
                }
            }
            return ans;
        }
        if(hr[h-1]>=vr[v-1]){
            return hr[h-1]*1LL*(v) + solve(h+1,v,m,n,hr,vr);
        }
        else{
            return vr[v-1]*1LL*(h) + solve(h,v+1,m,n,hr,vr);
        }
    }
    long long minimumCost(int m, int n, vector<int>& hr, vector<int>& vr) {
        sort(hr.begin(),hr.end(),greater<>());
        sort(vr.begin(),vr.end(),greater<>());
        return solve(1,1,m,n,hr,vr);
    }
};
