class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int, bool>> vals;
        for(int i: horizontalCut)
            vals.push_back({i, 1});
        for(int i: verticalCut)
            vals.push_back({i, 0});

        sort(vals.begin(), vals.end(), greater<pair<int, bool>>());

        long long hc = 0, vc = 0, res = 0;

        for(auto [c, d]: vals)
            if (d)
            {
                res += (vc+1)*c;
                hc++;
            }
            else
            {
                res += (hc+1)*c;
                vc++;
            }

        return res;
    }
};
