class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        res = 0
        h = 1
        v = 1
        horizontalCut.sort()
        verticalCut.sort()
        while verticalCut and horizontalCut:
            if horizontalCut[-1] > verticalCut[-1]:
                res += v*horizontalCut.pop()
                h += 1
            else:
                res += h*verticalCut.pop()
                v += 1
        if verticalCut:
            while verticalCut:
                res += h*verticalCut.pop()
                v += 1
        if horizontalCut:
            while horizontalCut:
                res += v*horizontalCut.pop()
                h += 1
        return res
