#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        int i=m-2;
        int j=n-2;
        int cnt1=1;
        int cnt2=1;
        ll ans=0;
        while(i>=0 && j>=0){
            if(h[i]>v[j]){
                ans+=((ll)cnt1*h[i]);
                cnt2++;i--;
            }
            else{
                ans+=((ll)cnt2*v[j]);
                cnt1++;j--;
            }
        }
        while(i>=0){
            ans+=((ll)cnt1*h[i]);
            i--;
        }
        while(j>=0){
            ans+=((ll)cnt2*v[j]);
            j--;
        }
        return ans;
    }
};
