class Solution:
    def minimumCost(self, m: int, n: int, hCut: List[int], vCut: List[int]) -> int:
        cuts = [(c, 'H') for c in hCut] + [(c, 'V') for c in vCut]
        cuts.sort(reverse=True, key=lambda x: x[0])
        hCnt = 1
        vCnt = 1
        cost = 0
        for c, typ in cuts:
            if typ == 'H':
                cost += c * vCnt
                hCnt += 1
            elif typ == 'V':
                cost += c * hCnt
                vCnt += 1
        return cost
