class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
        sort(verticalCut.begin(), verticalCut.end(), greater<int>());

        int hIdx = 0, vIdx = 0;
        int hPieces = 1, vPieces = 1;
        long long totalCost = 0;

        while (hIdx < horizontalCut.size() && vIdx < verticalCut.size()) {
            if (horizontalCut[hIdx] >= verticalCut[vIdx]) {
                totalCost += static_cast<long long>(horizontalCut[hIdx]) * vPieces;
                hPieces++;
                hIdx++;
            } else {
                totalCost += static_cast<long long>(verticalCut[vIdx]) * hPieces;
                vPieces++;
                vIdx++;
            }
        }

        while (hIdx < horizontalCut.size()) {
            totalCost += static_cast<long long>(horizontalCut[hIdx]) * vPieces;
            hPieces++;
            hIdx++;
        }


        while (vIdx < verticalCut.size()) {
            totalCost += static_cast<long long>(verticalCut[vIdx]) * hPieces;
            vPieces++;
            vIdx++;
        }

        return totalCost;
    }
};
