bool const helper(int a,int b){
    return a>b;
}
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int x=0,y=0,i=0,j=0;
        sort(horizontalCut.begin(),horizontalCut.end(),helper);
        sort(verticalCut.begin(),verticalCut.end(),helper);
        long long ans = 0;
        for(;i<horizontalCut.size() && j<verticalCut.size();){
            if(horizontalCut[i]>=verticalCut[j]) ans+=((y+1)*1ll*horizontalCut[i]),x++,i++;
            else ans+=((x+1)*1ll*verticalCut[j]),y++,j++;
        }
        while(j<verticalCut.size()) ans+=((x+1)*1ll*verticalCut[j]),y++,j++;
        while(i<horizontalCut.size()) ans+=((y+1)*1ll*horizontalCut[i]),x++,i++;
        return ans;
    }
};
