class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort( horizontalCut.rbegin(), horizontalCut.rend() );
        sort( verticalCut.rbegin(), verticalCut.rend() );


        horizontalCut.push_back(-10);
        verticalCut.push_back(-10);
        int h=0,v=0;

        long long cnt[2]; cnt[0]=cnt[1]=1;
        long long res = 0;
        while ( h < m-1 || v < n-1 ) {
            if ( horizontalCut[h] > verticalCut[v] ) {
                res += horizontalCut[h++] * 1ll * cnt[1];
                ++cnt[0];
            } else {
                res += verticalCut[v++] * 1ll * cnt[0];
                ++cnt[1];
            }
        }
        return res;
    }
};
