class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        long ans = 0;
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        int i=m-2,j=n-2;
        long h = 1;
        long v = 1;
        while(i >= 0 && j >= 0){

            if(horizontalCut[i] > verticalCut[j]){
                ans += h*horizontalCut[i];
                v++;
                i--;

            }else {
                ans += v * verticalCut[j];
                h++;
                j--;
            }
        }
        while (i>=0){
            ans += h*horizontalCut[i];
            i--;
        }
        while (j>=0){
            ans += v * verticalCut[j];
            j--;
        }
        return  ans;
    }
}
