#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {

    priority_queue<pair<ll, pair<ll, ll>>> q;

    ll hp = 1, vp = 1, ans = 0;

    int i = 0;

    for (auto &it : h)
    {
        q.push({it, {0, i++}});
    }

    i = 0;

    for (auto &it : v)
    {
        q.push({it, {1, i++}});
    }



    while (!q.empty())
    {
        pair<ll,pair<ll,ll>> tmp = q.top();
        q.pop();

        ll cost = tmp.first;
        ll type = tmp.second.first; 

        if (type == 0)
        {
            ans = 0LL + ans + (1LL * cost * vp);
            hp++;
        } 
        else if(type == 1)
        {
            ans = 0LL + ans + (1LL * cost * hp);
            vp++;
        }
    }

    return ans;
    }
};
