class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end(), greater<>());
        sort(verticalCut.begin(), verticalCut.end(), greater<>());
        long long int v = 1, h = 1;
        long long int vptr = 0, hptr = 0, cost = 0;
        while(vptr < verticalCut.size() && hptr < horizontalCut.size()){
            if(horizontalCut[hptr] > verticalCut[vptr]){
                cost += horizontalCut[hptr] * v;
                h++;
                hptr++;
            } else {
                cost += verticalCut[vptr] * h;
                v++;
                vptr++;
            }
        }
        while(vptr < verticalCut.size()) {
            cost += verticalCut[vptr] * h;
            vptr++;
        }
        while(hptr < horizontalCut.size()) {
            cost += horizontalCut[hptr] * v;
            hptr++;
        }
        return cost;
    }
};
