class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hor_cuts, ver_cuts = 1, 1
        costs = [(c, True) for c in horizontalCut] + [(c, False) for c in verticalCut]
        costs = sorted(costs, reverse=True)
        total = 0
        for c, is_hor in costs:
            if is_hor:
                hor_cuts += 1
                total += c * ver_cuts
            else:
                ver_cuts += 1
                total += c * hor_cuts
        return total
