import heapq
from typing import List

class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:

        max_heap = []

        for cost in horizontalCut:
            heapq.heappush(max_heap, (-cost, 'h'))
        for cost in verticalCut:
            heapq.heappush(max_heap, (-cost, 'v'))

        total_cost = 0
        horizontal_pieces = 1
        vertical_pieces = 1

        while max_heap:
            cost, direction = heapq.heappop(max_heap)
            cost = -cost  

            if direction == 'h':
                total_cost += cost * vertical_pieces
                horizontal_pieces += 1
            else:
                total_cost += cost * horizontal_pieces
                vertical_pieces += 1

        return total_cost
