typedef long long ll;
const int N = 5e4 + 5;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<vector<int>> infos;
        for(auto& cost : horizontalCut)
            infos.push_back({cost, 1});
        for(auto& cost : verticalCut)
            infos.push_back({cost, 2});
        sort(infos.begin(), infos.end());
        ll ans = 0, rc = 1, cc = 1;
        for(int i = infos.size() - 1; i >= 0; i--) {
            int cost = infos[i][0], type = infos[i][1];
            if(type == 1) {
                ans += cost * cc;
                rc++;
            } else {
                ans += cost * rc;
                cc++;
            }
        }
        return ans;
    }
};
