class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& A, vector<int>& B) {


        priority_queue<pair<long long, bool>> pq;
        for (auto h:A)
            pq.push({h, true});

        for (auto h:B)
            pq.push({h, false});

        long long a=1,b=1;
        long long ans=0;

        while (!pq.empty()) {

            long long cost=pq.top().first;
            long long che=pq.top().second;
            pq.pop();

            if (che) {
                ans+=cost*b;
                a++;
            } 
            else {
                ans+=cost*a;
                b++;
            }
        }

        return ans;

    }
};
