class Solution {
    using P = pair<int, int>;
    using T = long long;
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<P> a;
        a.reserve(m + n);
        for (int i : horizontalCut) {
            a.push_back({i, 0});
        }
        for (int i : verticalCut) {
            a.push_back({i, 1});
        }
        sort(a.begin(), a.end(), greater<P>());
        long long res = 0;
        long long c = 1, r = 1;
        for (auto &[x, y] : a) {
            if (y == 0) {
                c++;
                res += r * x;
            } else {
                r++;
                res += c * x;
            }
        }
        return res;
    }
};
