/**
 * @param {number} m
 * @param {number} n
 * @param {number[]} horizontalCut
 * @param {number[]} verticalCut
 * @return {number}
 */
var minimumCost = function(m, n, horizontalCut, verticalCut) {
    const MOD = 1e9 + 7;

    // Sort the cuts in descending order
    horizontalCut.sort((a, b) => b - a);
    verticalCut.sort((a, b) => b - a);

    let totalCost = 0;
    let horizontalParts = 1;
    let verticalParts = 1;

    let i = 0, j = 0;

    // Greedily choose the larger cut each time
    while (i < horizontalCut.length && j < verticalCut.length) {
        if (horizontalCut[i] > verticalCut[j]) {
            totalCost = (totalCost + horizontalCut[i] * verticalParts);
            horizontalParts++;
            i++;
        } else {
            totalCost = (totalCost + verticalCut[j] * horizontalParts);
            verticalParts++;
            j++;
        }
    }

    // Add remaining horizontal cuts
    while (i < horizontalCut.length) {
        totalCost = (totalCost + horizontalCut[i] * verticalParts);
        i++;
    }

    // Add remaining vertical cuts
    while (j < verticalCut.length) {
        totalCost = (totalCost + verticalCut[j] * horizontalParts);
        j++;
    }

    return totalCost;
};
