class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        priority_queue<int> hCuts(horizontalCut.begin(), horizontalCut.end());
        priority_queue<int> vCuts(verticalCut.begin(), verticalCut.end());

        long long int totalCost = 0;
        int horizontalPieces = 1;
        int verticalPieces = 1;

        while (!hCuts.empty() || !vCuts.empty()) {
            if (!hCuts.empty() && (vCuts.empty() || hCuts.top() >= vCuts.top())) {
                totalCost += hCuts.top() * verticalPieces;
                hCuts.pop();
                horizontalPieces++;
            } else {
                totalCost += vCuts.top() * horizontalPieces;
                vCuts.pop();
                verticalPieces++;
            }
        }

        return totalCost;
    }
};
