#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hori, vector<int>& vert) {

        sort(hori.rbegin(), hori.rend());
        sort(vert.rbegin(), vert.rend());

        int hp = 1;
        int vp = 1;

        int i = 0, j = 0;
        ll tots= 0;

        while (i < m - 1 && j < n - 1) {
            if (hori[i] > vert[j]) {
                tots += hori[i++] * vp;
                hp++;
            } else {
                tots += vert[j++] * hp;
                vp++;
            }
        }

        while (i < m - 1)
            tots += hori[i++] * vp;


        while (j < n - 1) 
            tots += vert[j++] * hp;

        return tots;
    }
};
