class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCuts, vector<int>& vCuts) {
        sort(hCuts.rbegin(), hCuts.rend());
        sort(vCuts.rbegin(), vCuts.rend());

        int hi = 0, vi = 0;
        int hCnt = 1, vCnt = 1;
        long long totalCost = 0;

        while (hi < hCuts.size() && vi < vCuts.size()) {
            if (hCuts[hi] >= vCuts[vi]) {
                totalCost += (long long)hCuts[hi] * vCnt;
                hCnt++;
                hi++;
            } else {
                totalCost += (long long)vCuts[vi] * hCnt;
                vCnt++;
                vi++;
            }
        }

        while (hi < hCuts.size()) {
            totalCost += (long long)hCuts[hi] * vCnt;
            hi++;
        }

        while (vi < vCuts.size()) {
            totalCost += (long long)vCuts[vi] * hCnt;
            vi++;
        }

        return totalCost;
    }
};
