#define ll long long
#define double long double
#define pb push_back
#define ff first
#define ss second
#define all(v) v.begin(),v.end()
#define allr(v) v.rbegin(),v.rend()
#define sz(v) (int)v.size()
#define deb(x) cout<<#x<<"="<<x<<endl;
#define pii pair<ll,ll>
#define vi vector<int>

class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& horizontalCut, vector<int>& verticalCut) 
    {
        vector<pii> h,v;
        for(ll i=0;i<sz(horizontalCut);i++) h.pb({horizontalCut[i],i});
        for(ll i=0;i<sz(verticalCut);i++) v.pb({verticalCut[i],i});
        sort(allr(h)); sort(allr(v));
        ll cuts_h=0,cuts_v=0,ans=0;
        ll i=0,j=0;
        while(i<sz(h) and j<sz(v))
        {
            if(h[i].ff>=v[j].ff)
            {
                ans+=h[i].ff*(cuts_v+1);
                cuts_h++;
                i++;
            }
            else
            {
                ans+=v[j].ff*(cuts_h+1);
                cuts_v++;
                j++;
            }
        }
        while(i<sz(h))
        {
            ans+=h[i].ff*(cuts_v+1);
            cuts_h++;
            i++;
        }
        while(j<sz(v))
        {
            ans+=v[j].ff*(cuts_h+1);
            cuts_v++;
            j++;
        }
        return ans;
    }
};
