#define ll long long int
class Solution
{
public:
    long long minimumCost(int m, int n, vector<int> &hori, vector<int> &vert)
    {
        priority_queue<pair<ll, pair<ll, ll>>> pq;

        ll horizontal = 1, vertical = 1, answer = 0;

        int i = 0;

        for (auto &it : hori)
        {
            pq.push({it, {0, i++}});
        }

        i = 0;

        for (auto &it : vert)
        {
            pq.push({it, {1, i++}});
        }

        while (!pq.empty())
        {
            pair<ll, pair<ll, ll>> tmp = pq.top();
            pq.pop();
            if (tmp.second.first == 0)
            {
                answer = 0LL + answer + (1LL * tmp.first * vertical);
                horizontal++;
            }
            else if (tmp.second.first == 1)
            {
                answer = 0LL + answer + (1LL * tmp.first * horizontal);
                vertical++;
            }
        }

        return answer;
    }
};
