class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         vector<pair<int, bool>> cuts;
        for (int cost : horizontalCut) {
            cuts.emplace_back(cost, true);
        }
        for (int cost : verticalCut) {
            cuts.emplace_back(cost, false);
        }
        sort(cuts.rbegin(), cuts.rend());
        int hPieces = 1;
        int vPieces = 1;
        long long totalCost = 0;
        for (auto& cut : cuts) {
            if (cut.second) { 
                totalCost += cut.first * vPieces;
                hPieces++;
            } else { 
                totalCost += cut.first * hPieces;
                vPieces++;
            }
        }
        return totalCost;
    }

};
