class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        gg,vp,hp,ans=[],1,1,0
        for i in range(m-1): gg.append((h[i],0))
        for j in range(n-1): gg.append((v[j],1))
        gg.sort(key=lambda x:-x[0])
        for val,ty in gg:
            if ty==0:
                ans+=val*vp
                hp+=1
                continue
            ans+=val*hp
            vp+=1
        return ans
