class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int, int>> ps;
        for (int e : horizontalCut) {
            ps.emplace_back(e, 0);
        }
        for (int e : verticalCut) {
            ps.emplace_back(e, 1);
        }
        int cnt0 = 0, cnt1 = 0;
        sort(ps.rbegin(), ps.rend());
        long long res = 0;
        for (auto [u, v] : ps) {
            if (v == 0) res += 1LL * (cnt1 + 1) * u, cnt0++;
            else res += 1LL * (cnt0 + 1) * u, cnt1++;
        }
        return res;
    }
};
