class Solution {
public:
    long long solve(vector<int>& h, vector<int>& v) {
        long long int hr=1,vr=1,ans=0;
        priority_queue<pair<int,int>>pq;
        for(auto c:h)pq.push({c,1});
        for(auto c:v)pq.push({c,2});
        while(!pq.empty()){
            long long int mul;
            if(pq.top().second==1)mul=vr,hr++;
            else mul=hr,vr++;
            ans+=mul*pq.top().first;
            pq.pop();

        }
        return ans;
    }
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        return solve(h,v);
    }
};
