class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>> v;v.reserve(m+n);
        for(int h : horizontalCut) v.push_back({h,0});
        for(int vv : verticalCut) v.push_back({vv,1});
        int cnt[2]{};
        ranges::sort(v,greater<>{});
        long long ans = 0;
        for(auto [num,pos] : v) {
            ans += 1ll * (cnt[pos ^ 1] + 1) * num;
            cnt[pos]++;
        }
        return ans;
    }
};
