class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ans = sum(horizontalCut) + m*sum(verticalCut)
        if m == 1 or n == 1:
            return ans
        horizontalCut.sort()
        verticalCut.sort(reverse = True)
        cumsum = horizontalCut[:]
        for i in range(1, len(cumsum)):
            cumsum[i] += cumsum[i-1]
        l = 0
        while l < len(horizontalCut) and horizontalCut[l] < verticalCut[0]:
            l += 1
        # print(horizontalCut)
        # print(verticalCut)
        # print(ans)
        for v in verticalCut:
            while l > 0 and horizontalCut[l-1] > v:
                l -= 1
            # print(v, ans, l)
            ans -= l * v
            if l > 0:
                ans += cumsum[l-1]
        return ans
