func minimumCost(m int, n int, horizontalCut []int, verticalCut []int) int64 {
    cuts := make([][2]int, 0, len(horizontalCut)+len(verticalCut))

    for _, cost := range horizontalCut {
        cuts = append(cuts, [2]int{cost, 0}) 
    }
    for _, cost := range verticalCut {
        cuts = append(cuts, [2]int{cost, 1})
    }

    sort.Slice(cuts, func(i, j int) bool {
        return cuts[i][0] > cuts[j][0]
    })

    totalCost := 0
    horizontalParts := 1
    verticalParts := 1

    for _, cut := range cuts {
        if cut[1] == 0 {
            totalCost += cut[0] * verticalParts
            horizontalParts++
        } else {
            totalCost += cut[0] * horizontalParts
            verticalParts++
        }
    }

    return int64(totalCost)
}
