class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        vector<pair<int,int>> v;
        for (int r:a) v.push_back({r,0});
        for (int r:b) v.push_back({r,1}); 
        sort(v.begin(),v.end(),greater<>());
        long long ans=0, x=1, y=1;
        for (auto [c,d]:v) {
            if (d) {
                ans+=x*c;
                y++;
            }
            else {
                ans+=y*c;
                x++;
            }
        }
        return ans;
    }
};
