class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        h.sort()
        v.sort()
        m1 = 1
        n1 = 1
        ans = 0
        while h and v:
            if h[-1] >= v[-1]:
                m1 += 1
                ans += n1 * h.pop()
            else:
                n1 += 1
                ans += m1 * v.pop()
        ans += sum(h) * n1 + sum(v) * m1
        return ans
