class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort()
        verticalCut.sort()
        ans, x, y = 0, 1, 1
        while horizontalCut or verticalCut:
            if horizontalCut and (not verticalCut or horizontalCut[-1] >= verticalCut[-1]):
                ans += horizontalCut.pop() * y
                x += 1
            else:
                ans += verticalCut.pop() * x
                y += 1
        return ans
