class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {

        sort(vcut.rbegin(), vcut.rend());


        long long tc=0;
        int vp=1, hp=1;

        size_t j=0,i=0;

        sort(hcut.rbegin(), hcut.rend());

        while(i<hcut.size() &&1&& j<vcut.size()) {
            if(hcut[i]>vcut[j]) {
                tc+=static_cast<long long>(hcut[i])*vp;
                i++;
                hp++;
            }
            else{
                tc+=static_cast<long long>(vcut[j])*hp;
                j++;
                vp++;
            }
        }

        while(1 &&i<hcut.size()) {
            tc+=static_cast<long long>(hcut[i])*vp;
            i++;
        }

        while(1&&j<vcut.size()) {
            tc+=static_cast<long long>(vcut[j])*hp;
            j++;
        }

        return tc;
    }
};
