class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<vector<int>> pq;
        for(int i=0;i<h.size();i++){
            pq.push({h[i],0,i});
        }
        for(int i=0;i<v.size();i++){
            pq.push({v[i],1,i});
        }
        int nh=1;
        int nv=1;
        long long ans=0;
        while(!pq.empty()){
            vector<int> x=pq.top();
            pq.pop();
            if(x[1]==0){
                nh++;
                ans+=1LL*x[0]*nv;
            }
            else{
                nv++;
                ans+=1LL*x[0]*nh;
            }
        }
        return ans;
    }
};
